def test_solve_quadratic():
    from sympy import symbols, solve, Eq
    x = symbols('x')
    solutions = solve(Eq(x**2 - 5*x + 6, 0), x)
    assert set(solutions) == {2, 3}

def test_post_with_auth(mock_session):
    headers = {'Authorization': 'Bearer token123'}
    payload = {'key': 'value'}
    mock_session.post.return_value.status_code = 201
    result = mock_session.post('http://api.example.com/create', json=payload, headers=headers)
    assert result.status_code == 201
    mock_session.post.assert_called_once()
